mapper.select:
select count(*) from tbl_activity a join tbl_user b on a.owner=b.id


    a.name like concat('%',#{name},'%')


   and b.name like concat('%',#{owner},'%')


   and a.startDate >= #{startDate}


   and a.endDate <= #{endDate}

select a.id, a.name, b.name as owner, a.startDate, a.endDate from tbl_activity a join tbl_user b on a.owner=b.id
a.name like concat('%',#{name},'%')
and b.name like concat('%',#{owner},'%')
and a.startDate >= #{startDate}
and a.endDate <= #{endDate}
ORDER BY startDate DESC LIMIT #{pageNum},#{pagesize}
